import React, {useState,useContext} from 'react';

import{
  CREATE_EVENT,
  DELETE_ALL_EVENTS,
  ADD_OPERATION_LOG,
  DELETE_ALL_OPERATION_LOGS
} from '../actions';
import AppContext from '../contexts/AppContext';
import {timeCurrentISO8601} from '../utils';

const EventForm = () =>{
  const {state,dispatch} = useContext(AppContext);
  const [title,setTitle] = useState('');
  const [body,setBody] = useState('');

  const addEvent = e =>{//eventを拾う
    e.preventDefault();//reroadしない
    dispatch({
      type:CREATE_EVENT,
      title,//title:title
      body//body:body
    });

    dispatch({
      type:ADD_OPERATION_LOG,
      description:'イベントを作成しました。',
      operatedAt:timeCurrentISO8601()
    });
    setTitle('');
    setBody('');
  }

  const deleteAllEvents = e =>{
    e.preventDefault();
    const result = window.confirm('全てのイベントを本当に削除しても良いですか？');
    if(result){
      dispatch({type:DELETE_ALL_EVENTS});
      dispatch({
        type:ADD_OPERATION_LOG,
        description:'全てのイベントを削除しました。',
        operatedAt:timeCurrentISO8601()
      });
    }
  }

  const unCreatable = title === '' || body === '';

  const deleteAllOperationLogs = e => {

  }

  return(
    <>
    <h4>イベントフォーム作成</h4>
    <form>
      <div className="form-group">
        <label htmlFor="formEventTitle">タイトル</label>
        <input className="form-control" id="formEventTitle" value={title} onChange={e => setTitle(e.target.value)} />
      </div>

      <div className="form-group">
        <label htmlFor="formEventBody">ボディー</label>
        <input className="form-control" id="formEventBody" value={body} onChange={e => setBody(e.target.value)} />
      </div>

      <button className="btn btn-primary" onClick={addEvent} disabled={unCreatable}>イベントを作成する</button>
      <button className="btn btn-danger" onClick={deleteAllEvents} disabled={state.events.length === 0}>全てのイベントを削除する</button>
      <button className="btn btn-danger" onClick={deleteAllOperationLogs}>全ての操作ログを削除する</button>
    </form>
    </>
  )
}

export default EventForm;
