import React, {useState,useReducer} from 'react';

import 'bootstrap/dist/css/bootstrap.min.css';

import Event from './Event';
import reducer from '../reducers';

const App = () => {
  const [state,dispatch] = useReducer(reducer,[]);
  const [title,setTitle] = useState('');
  const [body,setBody] = useState('');

  const addEvent = e =>{//eventを拾う
    e.preventDefault();//reroadしない
    dispatch({
      type:'CREATE_EVENT',
      title,//title:title
      body//body:body
    });
    setTitle('');
    setBody('');
  }

  const deleteAllEvents = e =>{
    e.preventDefault();

  }

  return (
     <div className="container-fluid">
       <h4>イベントフォーム作成</h4>
       <form>
         <div className="form-group">
           <label htmlFor="formEventTitle">タイトル</label>
           <input className="form-control" id="formEventTitle" value={title} onChange={e => setTitle(e.target.value)} />
         </div>

         <div className="form-group">
           <label htmlFor="formEventBody">ボディー</label>
           <input className="form-control" id="formEventBody" value={body} onChange={e => setBody(e.target.value)} />
         </div>

         <button className="btn btn-primary" onClick={addEvent}>イベントを作成する</button>
         <button className="btn btn-danger" onClick={deleteAllEvents}>全てのイベントを削除する</button>

       </form>

       <h4>イベントフォーム作成</h4>
       <table className="table table-hover">
         <thead>
          <tr>
            <th>ID</th>
            <th>タイトル</th>
            <th>ボディー</th>
            <th></th>
          </tr>
         </thead>
         <tbody>
         {state.map((event,index)=>(<Event key={index} event={event} dispatch={dispatch} />))}
         </tbody>
       </table>
     </div>
  );
}

export default App;
